app.controller("newGameCtrl",["$scope","$location","$route","$rootScope",function($scope,$location,$route,$rootScope){$scope.steps=[{no:1,name:"Data"},{no:2,name:"Tasks"},{no:3,name:"Skin"},{no:4,name:"Publish"}];$scope.selection=$scope.steps[0];$scope.getCurrentStepIndex=function(){return _.indexOf($scope.steps,$scope.selection)};$scope.isDisabled=function(index){if(index>$scope.getCurrentStepIndex()+1||$scope.form.$invalid&&index>$scope.getCurrentStepIndex())return true};$scope.isLast=function(){return!$scope.hasNextStep()};
$scope.goToStep=function(index){if(!_.isUndefined($scope.steps[index]))return $scope.selection=$scope.steps[index]};$scope.hasNextStep=function(){var nextStep;var stepIndex;stepIndex=$scope.getCurrentStepIndex();nextStep=stepIndex+1;return!_.isUndefined($scope.steps[nextStep])};$scope.hasPreviousStep=function(){var previousStep;var stepIndex;stepIndex=$scope.getCurrentStepIndex();previousStep=stepIndex-1;return!_.isUndefined($scope.steps[previousStep])};$scope.incrementStep=function(){var nextStep;
var stepIndex;if($scope.hasNextStep()){stepIndex=$scope.getCurrentStepIndex();nextStep=stepIndex+1;return $scope.selection=$scope.steps[nextStep]}};$scope.decrementStep=function(){var previousStep;var stepIndex;if($scope.hasPreviousStep()){stepIndex=$scope.getCurrentStepIndex();previousStep=stepIndex-1;return $scope.selection=$scope.steps[previousStep]}};$scope.saveit=function(){return alert("Here this project will be saved")};return $scope.publishit=function(){$scope.saveit();return alert("and published too")}}]);